/**
 * Assumes convert() returns some non-null instance of getExpectedType().
 */
@Test
void testConvertDate() {
    final String[] message = { "from Date", "from Calendar", "from SQL Date", "from SQL Time", "from SQL Timestamp", "from LocalDate", "from LocalDateTime", "from ZonedDateTime", "from OffsetDateTime" };
    final long nowMillis = System.currentTimeMillis();
    final Object[] date = { new Date(nowMillis), new java.util.GregorianCalendar(), new java.sql.Date(nowMillis), new java.sql.Time(nowMillis), new java.sql.Timestamp(nowMillis), Instant.ofEpochMilli(nowMillis).atZone(ZoneId.systemDefault()).toLocalDate().atStartOfDay(ZoneId.systemDefault()).toLocalDate(), Instant.ofEpochMilli(nowMillis).atZone(ZoneId.systemDefault()).toLocalDateTime(), ZonedDateTime.ofInstant(Instant.ofEpochMilli(nowMillis), ZoneId.systemDefault()), OffsetDateTime.ofInstant(Instant.ofEpochMilli(nowMillis), ZoneId.systemDefault()) };
    // Initialize calendar also with same ms to avoid a failing test in a new time slice
    ((GregorianCalendar) date[1]).setTime(new Date(nowMillis));
    for (int i = 0; i < date.length; i++) {
        final Class<T> expectedType = getExpectedType();
        final Object val = makeConverter().convert(expectedType, date[i]);
        assertNotNull(val, "Convert " + message[i] + " should not be null");
        assertInstanceOf(expectedType, val, "Convert " + message[i] + " should return a " + expectedType.getName());
        long test = nowMillis;
        if (date[i] instanceof LocalDate || val instanceof LocalDate) {
            test = Instant.ofEpochMilli(nowMillis).atZone(ZoneId.systemDefault()).toLocalDate().atStartOfDay(ZoneId.systemDefault()).toInstant().toEpochMilli();
        }
        assertEquals(test, getTimeInMillis(val), "Convert " + message[i] + " should return a " + date[0]);
    }
}

