void testGetOptionValue(final String[] args, final Option opt, final OptionGroup optionGroup, final boolean optDep, final String optValue, final boolean grpDep, final String grpValue, final Option grpOpt) throws ParseException {
    final Options options = new Options().addOptionGroup(optionGroup);
    final List<Option> handler = new ArrayList<>();
    final CommandLine commandLine = DefaultParser.builder().setDeprecatedHandler(handler::add).get().parse(options, args);
