    final Option c = Option.builder().option("c").longOpt("third-letter").get();
    final Options options = new Options();
    options.addOption(a);
    options.addOption(b);
    options.addOption(c);
    // -d is rogue option
    final String[] args = { "-a", "-b", "-c", "-d", "arg1", "arg2" };
