@Test
void testDetect() throws Exception {
    for (final String extension : new String[] { ArchiveStreamFactory.AR, ArchiveStreamFactory.ARJ, ArchiveStreamFactory.CPIO, ArchiveStreamFactory.DUMP, // Compress doesn't know how to detect JARs, see COMPRESS-91
    // ArchiveStreamFactory.JAR,
    ArchiveStreamFactory.SEVEN_Z, ArchiveStreamFactory.TAR, ArchiveStreamFactory.ZIP }) {
        assertEquals(extension, detect("bla." + extension));
    }
    final ArchiveException e1 = assertThrows(ArchiveException.class, () -> ArchiveStreamFactory.detect(new BufferedInputStream(new ByteArrayInputStream(ArrayUtils.EMPTY_BYTE_ARRAY))), "shouldn't be able to detect empty stream");
    assertEquals("No Archiver found for the stream signature", e1.getMessage());
    final IllegalArgumentException e2 = assertThrows(IllegalArgumentException.class, () -> ArchiveStreamFactory.detect(null), "shouldn't be able to detect null stream");
    assertEquals("Stream must not be null.", e2.getMessage());
    final ArchiveException e3 = assertThrows(ArchiveException.class, () -> ArchiveStreamFactory.detect(new BufferedInputStream(new BrokenInputStream())), "Expected ArchiveException");
    assertEquals("Failure reading signature.", e3.getMessage());
}

