@Test
void testPaxTimeFieldsForInvalidValues() {
    final String[] headerNames = { "LIBARCHIVE.creationtime", "atime", "mtime", "ctime" };
    // @formatter:off
    final String[] testValues = { // Generate a number with a very large integer or fractional component
    new Random().nextLong() + "." + String.join("", Collections.nCopies(15000, String.valueOf(Long.MAX_VALUE))), // These two examples use the exponent notation
    "9e9999999", "9E9999999", // These examples are out of range for java.time.Instant
    String.valueOf(Long.MAX_VALUE), String.valueOf(Long.MIN_VALUE) };
    // @formatter:on
    final TarArchiveEntry entry = new TarArchiveEntry("test.txt");
    for (final String name : headerNames) {
        for (final String value : testValues) {
            final Exception exp = assertThrows(IllegalArgumentException.class, () -> entry.addPaxHeader(name, value));
            assertTrue(exp.getCause().getMessage().startsWith("Corrupted PAX header. Time field value is invalid"));
        }
    }
}

