    }
    assertTrue(buffer.available(), "available");
    for (int i = 0; i < size / 2; i++) {
        assertEquals(i, buffer.get(), "buffer[" + i + "]");
    }
    assertEquals(-1, buffer.get());
    assertFalse(buffer.available(), "available");
}

