/**
 * If you want to see a MemoryLimitException, then run with {@code -Xmx64m}.
 */
@Test
void test() throws IOException {
    final String fixture = "target/ZipArchiveInputStreamMalformedTest.bin";
    try (FileOutputStream fos = new FileOutputStream(fixture)) {
        // Local File Header (LFH).
        // size of LFH without filename/extra.
        final ByteBuffer buffer = ByteBuffer.allocate(30);
        buffer.order(ByteOrder.LITTLE_ENDIAN);
        // Local file header signature.
        buffer.putInt(0x04034B50);
        // version needed to extract.
        buffer.putShort((short) 20);
        // general purpose bit flag (bit 3 set).
        buffer.putShort((short) 0x08);
        // compression method.
        buffer.putShort((short) 0);
        // last mod file time.
        buffer.putShort((short) 0);
        // last mod file date.
        buffer.putShort((short) 0);
        // CRC-32.
        buffer.putInt(0);
        // compressed size.
        buffer.putInt(0);
        // uncompressed size.
        buffer.putInt(0);
        // file name length.
        buffer.putShort((short) 1);
        // extra field length.
        buffer.putShort((short) 0);
        fos.write(buffer.array());
        // Filename: "a".
        fos.write('a');
        // Payload: 110 MB of 'A'.
        // 1 MB chunk.
        final byte[] chunk = new byte[1024 * 1024];
        Arrays.fill(chunk, (byte) 'A');
        for (int i = 0; i < 110; i++) {
            fos.write(chunk);
        }
    }
    try (ZipArchiveInputStream zis = ZipArchiveInputStream.builder().setFile(fixture).setSupportStoredEntryDataDescriptor(true).get()) {
        zis.getNextEntry();
        assertThrows(CompressException.class, () -> IOUtils.toByteArray(zis));
    }
}

