        InputStream inputStream = Channels.newInputStream(channel);
        ZipArchiveInputStream splitInputStream = ZipArchiveInputStream.builder().setInputStream(inputStream).setSkipSplitSignature(true).get()) {
        final File fileToCompare = getFile("COMPRESS-477/split_zip_created_by_winrar/zip_to_compare_created_by_winrar.zip");
