@Test
void testDecodeFixedHuffmanBlockWithMemoryLookupInSmallBuffer() throws Exception {
    final byte[] data = { // |--- binary filling ---|76543210
    // final block + fixed huffman + H
    0b11111111111111111111111111110011, // H + e
    0b00000000000000000000000001001000, // e + l
    0b11111111111111111111111111001101, // l + l
    0b11111111111111111111111111001001, // l + o
    0b11111111111111111111111111001001, // o + ' '
    0b00000000000000000000000001010111, // ' ' + W
    0b00000000000000000000000000001000, // W + o
    0b11111111111111111111111111001111, // o + r
    0b00000000000000000000000000101111, // r + l
    0b11111111111111111111111111001010, // l + d
    0b00000000000000000000000001001001, // d + '\n'
    0b11111111111111111111111111100001, // '\n' + <len>
    0b00000000000000000000000000100010, // <len> + offset <001> + dist6
    0b11111111111111111111111110000110, // dist6 + offset <11> + end of block (000000)
    0b00000000000000000000000000001101, // end of block (0000) + garbage
    0b11111111111111111111111111111000 };
    try (HuffmanDecoder decoder = new HuffmanDecoder(new ByteArrayInputStream(data))) {
        final byte[] result = new byte[30];
        int len;
        len = decoder.decode(result);
        assertEquals(30, len);
        assertEquals("Hello World\nHello World\nHello ", new String(result, 0, len));
        len = decoder.decode(result);
        assertEquals(18, len);
        assertEquals("World\nHello World\n", new String(result, 0, len));
    }
}

@Test
void testDecodeSimpleFixedHuffmanBlockToSmallBuffer() throws Exception {
    final byte[] data = { // |--- binary filling ---|76543210
    // final block + fixed huffman + H
    0b11111111111111111111111111110011, // H + e
    0b00000000000000000000000001001000, // e + l
    0b11111111111111111111111111001101, // l + l
    0b11111111111111111111111111001001, // l + o
    0b11111111111111111111111111001001, // o + ' '
    0b00000000000000000000000001010111, // ' ' + W
    0b00000000000000000000000000001000, // W + o
    0b11111111111111111111111111001111, // o + r
    0b00000000000000000000000000101111, // r + l
    0b11111111111111111111111111001010, // l + d
    0b00000000000000000000000001001001, // d + end of block
    0b00000000000000000000000000000001, // end of block (00) + garbage
    0b11111111111111111111111111111100 };
    try (HuffmanDecoder decoder = new HuffmanDecoder(new ByteArrayInputStream(data))) {
        final byte[] result = new byte[10];
        int len;
        len = decoder.decode(result);
        assertEquals(10, len);
        assertEquals("Hello Worl", new String(result, 0, len));
        len = decoder.decode(result);
        assertEquals(1, len);
        assertEquals("d", new String(result, 0, len));
    }
}

