void testDecodeUncompressedBlockWithInvalidLenNLenValue() throws Exception {
    final byte[] data = { // end of block + no compression mode
    0b1, // len & ~len
    11, // len & ~len
    0, // len & ~len
    -12, // len & ~len
    -2, 'H', 'e', 'l', 'l', 'o', ' ', 'W', 'o', 'r', 'l', 'd' };
