@Test
void testRejectsBlocksWithoutChecksum() {
    final byte[] input = { // signature
    4, // signature
    0x22, // signature
    0x4d, // signature
    0x18, // flag - Version 01, block independent, with block checksum, no content size, no content checksum
    0x70, // block size 4MB
    0x70, // checksum
    114, // 13 bytes length and uncompressed bit set
    13, // 13 bytes length and uncompressed bit set
    0, // 13 bytes length and uncompressed bit set
    0, // 13 bytes length and uncompressed bit set
    (byte) 0x80, // content
    'H', // content
    'e', // content
    'l', // content
    'l', // content
    'o', // content
    ',', // content
    ' ', // content
    'w', // content
    'o', // content
    'r', // content
    'l', // content
    'd', // content
    '!' };
    final IOException ex = assertThrows(CompressorException.class, () -> {
        try (InputStream a = new FramedLZ4CompressorInputStream(new ByteArrayInputStream(input))) {
            IOUtils.toByteArray(a);
        }
    }, "expected exception");
    assertTrue(ex.getMessage().contains("block checksum"));
}

