@Test
void testWriteByteArrayVsWriteByte() throws IOException {
    ByteArrayOutputStream buffer = new ByteArrayOutputStream();
    final byte[] bytes = "abcdefghijklmnop".getBytes();
    try (FramedLZ4CompressorOutputStream compressor = new FramedLZ4CompressorOutputStream(buffer, new FramedLZ4CompressorOutputStream.Parameters(FramedLZ4CompressorOutputStream.BlockSize.K64, true, false, false))) {
        compressor.write(bytes);
        compressor.finish();
        compressor.close();
        assertTrue(compressor.isClosed());
    }
    final byte[] bulkOutput = buffer.toByteArray();
    buffer = new ByteArrayOutputStream();
    try (FramedLZ4CompressorOutputStream compressor = new FramedLZ4CompressorOutputStream(buffer, new FramedLZ4CompressorOutputStream.Parameters(FramedLZ4CompressorOutputStream.BlockSize.K64, true, false, false))) {
        for (final byte element : bytes) {
            compressor.write(element);
        }
        compressor.finish();
        compressor.close();
        assertTrue(compressor.isClosed());
    }
    assertTrue(Arrays.equals(bulkOutput, buffer.toByteArray()));
}

