@Test
void testMatchesSkippableFrame() {
    final byte[] data = { 0, (byte) 0x2A, (byte) 0x4D, (byte) 0x18 };
    assertFalse(ZstdUtils.matches(data, 4));
    for (byte b = (byte) 0x50; b < 0x60; b++) {
        data[0] = b;
        assertTrue(ZstdUtils.matches(data, 4));
    }
    assertFalse(ZstdUtils.matches(data, 3));
    assertTrue(ZstdUtils.matches(data, 5));
}

@Test
void testMatchesZstandardFrame() {
    final byte[] data = { (byte) 0x28, (byte) 0xB5, (byte) 0x2F, (byte) 0xFD };
    assertFalse(ZstdUtils.matches(data, 3));
    assertTrue(ZstdUtils.matches(data, 4));
    assertTrue(ZstdUtils.matches(data, 5));
    data[3] = '0';
    assertFalse(ZstdUtils.matches(data, 4));
}

@Test
void testMatches() {
    assertFalse(ZCompressorInputStream.matches(new byte[] { 1, 2, 3, 4 }, 4));
    assertFalse(ZCompressorInputStream.matches(new byte[] { 0x1f, 2, 3, 4 }, 4));
    assertFalse(ZCompressorInputStream.matches(new byte[] { 1, (byte) 0x9d, 3, 4 }, 4));
    assertFalse(ZCompressorInputStream.matches(new byte[] { 0x1f, (byte) 0x9d, 3, 4 }, 3));
    assertTrue(ZCompressorInputStream.matches(new byte[] { 0x1f, (byte) 0x9d, 3, 4 }, 4));
}

