/**
 * @see "https://issues.apache.org/jira/browse/COMPRESS-363"
 */
@Test
void testLittleEndianWithOverflow() throws Exception {
    final ByteArrayInputStream in = new ByteArrayInputStream(new byte[] { // 01010111
    87, // 00101101
    45, // 01000010
    66, // 00001111
    15, // 01011010
    90, // 00011101
    29, // 01011000
    88, // 00111101
    61, // 00100001
    33, // 01001010
    74 });
    try (BitInputStream bin = new BitInputStream(in, ByteOrder.LITTLE_ENDIAN)) {
        // 10111
        assertEquals(// 10111
        23, bin.readBits(5));
        // 0001-00111101-01011000-00011101-01011010-00001111-01000010-00101101-010
        assertEquals(// 0001-00111101-01011000-00011101-01011010-00001111-01000010-00101101-010
        714595605644185962L, bin.readBits(63));
        // 01001010-0010
        assertEquals(// 01001010-0010
        1186, bin.readBits(12));
        assertEquals(-1, bin.readBits(1));
    }
}

