void testSkip() throws IOException {
    final CRC32 crc32 = new CRC32();
    final byte[] byteArray = new byte[4];
    final ByteArrayInputStream byteArrayInputStream = new ByteArrayInputStream(byteArray);
    try (ChecksumVerifyingInputStream checksumVerifyingInputStream = new ChecksumVerifyingInputStream(crc32, byteArrayInputStream, (byte) 33, 2303L)) {
        @SuppressWarnings("unused")
        final int intOne = checksumVerifyingInputStream.read(byteArray);
        final long skipReturnValue = checksumVerifyingInputStream.skip((byte) 1);
        assertEquals(558161692L, crc32.getValue());
        assertEquals(0, byteArrayInputStream.available());
        assertArrayEquals(new byte[4], byteArray);
        assertEquals(0L, skipReturnValue);
    }
}

@Test
void testInput() throws Exception {
    // I don't like "test all at once" tests either, but the class
    // is so trivial
    final ByteArrayInputStream bis = new ByteArrayInputStream(new byte[] { 1, 2, 3, 4 });
    try (CountingInputStream i = new CountingInputStream(bis)) {
        assertEquals(1, i.read());
        assertEquals(1, i.getBytesRead());
        byte[] b = new byte[2];
        i.read(b);
        assertEquals(3, i.getBytesRead());
        assertArrayEquals(new byte[] { 2, 3 }, b);
        b = new byte[3];
        i.read(b, 1, 1);
        assertArrayEquals(new byte[] { 0, 4, 0 }, b);
        assertEquals(4, i.getBytesRead());
        i.count(-1);
        assertEquals(4, i.getBytesRead());
        i.count(-2);
        assertEquals(2, i.getBytesRead());
    }
}

@Test
void testOutput() throws Exception {
    // I don't like "test all at once" tests either, but the class
    // is so trivial
    final ByteArrayOutputStream bos = new ByteArrayOutputStream();
    try (CountingOutputStream o = new CountingOutputStream(bos)) {
        o.write(1);
        assertEquals(1, o.getBytesWritten());
        o.write(new byte[] { 2, 3 });
        assertEquals(3, o.getBytesWritten());
        o.write(new byte[] { 2, 3, 4, 5 }, 2, 1);
        assertEquals(4, o.getBytesWritten());
        o.count(-1);
        assertEquals(4, o.getBytesWritten());
        o.count(-2);
        assertEquals(2, o.getBytesWritten());
    }
    assertArrayEquals(new byte[] { 1, 2, 3, 4 }, bos.toByteArray());
}

