    final int offset = 123;
    for (int i = 0; i < results.length; i++) {
        map.put(results[i], i + offset);
    }
    for (int i = 0; i < results.length; i++) {
        Assertions.assertEquals(i + offset, map.get(results[i]));
    }
}

