/**
 * Tests replace with fail on undefined variable.
 */
@Test
void testReplaceFailOnUndefinedVariable() throws IOException {
    values.put("animal.1", "fox");
    values.put("animal.2", "mouse");
    values.put("species", "2");
    final StringSubstitutor sub = new StringSubstitutor(values);
    sub.setEnableUndefinedVariableException(true);
    assertEquals("Cannot resolve variable 'animal.${species' (enableSubstitutionInVariables=false).", assertThrows(IllegalArgumentException.class, () -> replace(sub, "The ${animal.${species}} jumps over the ${target}.")).getMessage());
    assertEquals("Cannot resolve variable 'animal.${species:-1' (enableSubstitutionInVariables=false).", assertThrows(IllegalArgumentException.class, () -> replace(sub, "The ${animal.${species:-1}} jumps over the ${target}.")).getMessage());
    assertEquals("Cannot resolve variable 'unknown' (enableSubstitutionInVariables=false).", assertThrows(IllegalArgumentException.class, () -> replace(sub, "The ${test:-statement} is a sample for missing ${unknown}.")).getMessage());
    // if default value is available, exception will not be thrown
    assertEqualsCharSeq("The statement is a sample for missing variable.", replace(sub, "The ${test:-statement} is a sample for missing ${unknown:-variable}."));
    assertEqualsCharSeq("The fox jumps over the lazy dog.", replace(sub, "The ${animal.1} jumps over the ${target}."));
}

/**
 * Tests whether replace with fail on undefined variable with substitution in variable names enabled.
 */
@Test
void testReplaceFailOnUndefinedVariableWithReplaceInVariable() throws IOException {
    values.put("animal.1", "fox");
    values.put("animal.2", "mouse");
    values.put("species", "2");
    values.put("statement.1", "2");
    values.put("recursive", "1");
    values.put("word", "variable");
    values.put("testok.2", "statement");
    final StringSubstitutor sub = new StringSubstitutor(values);
    sub.setEnableUndefinedVariableException(true);
    sub.setEnableSubstitutionInVariables(true);
    assertEqualsCharSeq("The mouse jumps over the lazy dog.", replace(sub, "The ${animal.${species}} jumps over the ${target}."));
    values.put("species", "1");
    assertEqualsCharSeq("The fox jumps over the lazy dog.", replace(sub, "The ${animal.${species}} jumps over the ${target}."));
    // exception is thrown here because variable with name test.1 is missing
    assertEquals("Cannot resolve variable 'statement' (enableSubstitutionInVariables=true).", assertThrows(IllegalArgumentException.class, () -> replace(sub, "The ${test.${statement}} is a sample for missing ${word}.")).getMessage());
    // exception is thrown here because variable with name test.2 is missing
    assertEquals("Cannot resolve variable 'test.2' (enableSubstitutionInVariables=true).", assertThrows(IllegalArgumentException.class, () -> replace(sub, "The ${test.${statement.${recursive}}} is a sample for missing ${word}.")).getMessage());
    assertEqualsCharSeq("statement", replace(sub, "${testok.${statement.${recursive}}}"));
    assertEqualsCharSeq("${testok.2}", replace(sub, "$${testok.${statement.${recursive}}}"));
    assertEqualsCharSeq("The statement is a sample for missing variable.", replace(sub, "The ${testok.${statement.${recursive}}} is a sample for missing ${word}."));
}

