@Test
void testDefaultInterpolator() {
    // Used to cut and paste into the docs.
    // @formatter:off
    final StringSubstitutor interpolator = StringSubstitutor.createInterpolator();
    final String text = interpolator.replace("Base64 Decoder:        ${base64Decoder:SGVsbG9Xb3JsZCE=}\n" + "Base64 Encoder:        ${base64Encoder:HelloWorld!}\n" + "Java Constant:         ${const:java.awt.event.KeyEvent.VK_ESCAPE}\n" + "Date:                  ${date:yyyy-MM-dd}\n" + "Environment Variable:  ${env:USERNAME}\n" + "File Content:          ${file:UTF-8:src/test/resources/org/apache/commons/text/document.properties}\n" + "Java:                  ${java:version}\n" + "Localhost:             ${localhost:canonical-name}\n" + "Properties File:       ${properties:src/test/resources/org/apache/commons/text/document.properties::mykey}\n" + "Resource Bundle:       ${resourceBundle:org.apache.commons.text.example.testResourceBundleLookup:mykey}\n" + "System Property:       ${sys:user.dir}\n" + "URL Decoder:           ${urlDecoder:Hello%20World%21}\n" + "URL Encoder:           ${urlEncoder:Hello World!}\n" + "XML XPath:             ${xml:src/test/resources/org/apache/commons/text/document.xml:/root/path/to/node}\n");
    // @formatter:on
    Assertions.assertNotNull(text);
    // TEXT-171:
    Assertions.assertFalse(text.contains("${base64Decoder:SGVsbG9Xb3JsZCE=}"));
    Assertions.assertFalse(text.contains("${base64Encoder:HelloWorld!}"));
    Assertions.assertFalse(text.contains("${urlDecoder:Hello%20World%21}"));
    Assertions.assertFalse(text.contains("${urlEncoder:Hello World!}"));
    Assertions.assertFalse(text.contains("${resourceBundle:org.apache.commons.text.example.testResourceBundleLookup:mykey}"));
}

