/**
 * Tests whether a variable can be replaced in a variable name.
 */
@Test
void testReplaceInVariable() {
    values.put("animal.1", "fox");
    values.put("animal.2", "mouse");
    values.put("species", "2");
    final StrSubstitutor sub = new StrSubstitutor(values);
    sub.setEnableSubstitutionInVariables(true);
    assertEquals("The mouse jumps over the lazy dog.", sub.replace("The ${animal.${species}} jumps over the ${target}."));
    values.put("species", "1");
    assertEquals("The fox jumps over the lazy dog.", sub.replace("The ${animal.${species}} jumps over the ${target}."));
    assertEquals("The fox jumps over the lazy dog.", sub.replace("The ${unknown.animal.${unknown.species:-1}:-fox} " + "jumps over the ${unknow.target:-lazy dog}."));
}

