@Test
void testAsReader() throws Exception {
    final TextStringBuilder sb = new TextStringBuilder("some text");
    try (Reader reader = sb.asReader()) {
        assertTrue(reader.ready());
        final char[] buf = new char[40];
        assertEquals(9, reader.read(buf));
        assertEquals("some text", new String(buf, 0, 9));
        assertEquals(-1, reader.read());
        assertFalse(reader.ready());
        assertEquals(0, reader.skip(2));
        assertEquals(0, reader.skip(-1));
        assertTrue(reader.markSupported());
    }
    try (Reader reader = sb.asReader()) {
        assertEquals('s', reader.read());
        reader.mark(-1);
        final char[] array = new char[3];
        assertEquals(3, reader.read(array, 0, 3));
        assertEquals('o', array[0]);
        assertEquals('m', array[1]);
        assertEquals('e', array[2]);
        reader.reset();
        assertEquals(1, reader.read(array, 1, 1));
        assertEquals('o', array[0]);
        assertEquals('o', array[1]);
        assertEquals('e', array[2]);
        assertEquals(2, reader.skip(2));
        assertEquals(' ', reader.read());
        assertTrue(reader.ready());
        reader.close();
        assertTrue(reader.ready());
    }
    try (Reader reader = sb.asReader()) {
        final char[] array = new char[3];
        assertThrows(IndexOutOfBoundsException.class, () -> reader.read(array, -1, 0));
        assertThrows(IndexOutOfBoundsException.class, () -> reader.read(array, 0, -1));
        assertThrows(IndexOutOfBoundsException.class, () -> reader.read(array, 100, 1));
        assertThrows(IndexOutOfBoundsException.class, () -> reader.read(array, 0, 100));
        assertThrows(IndexOutOfBoundsException.class, () -> reader.read(array, Integer.MAX_VALUE, Integer.MAX_VALUE));
        assertEquals(0, reader.read(array, 0, 0));
        assertEquals(0, array[0]);
        assertEquals(0, array[1]);
        assertEquals(0, array[2]);
        reader.skip(9);
        assertEquals(-1, reader.read(array, 0, 1));
        reader.reset();
        final char[] array2 = new char[30];
        assertEquals(9, reader.read(array2, 0, 30));
    }
}

