    assertEquals("", WordUtils.capitalizeFully("", ArrayUtils.EMPTY_CHAR_ARRAY));
    assertEquals("  ", WordUtils.capitalizeFully("  ", ArrayUtils.EMPTY_CHAR_ARRAY));
    char[] chars = { '-', '+', ' ', '@' };
    assertEquals("I", WordUtils.capitalizeFully("I", chars));
    assertEquals("I", WordUtils.capitalizeFully("i", chars));
    assertEquals("I-Am Here+123", WordUtils.capitalizeFully("i-am here+123", chars));
    assertEquals("I Am+Here-123", WordUtils.capitalizeFully("I Am+Here-123", chars));
