    final String usPlus3Decimal = NumberFormat.getCurrencyInstance(Locale.US).format(1234.56) + "7";
    final String usMinus = NumberFormat.getCurrencyInstance(Locale.US).format(-1234.56);
    assertEquals(expected, validator.validate(ukPlus), "Default locale");
    assertEquals(expected, validator.validate(ukPlus, Locale.UK), "UK locale");
    assertEquals(negative, validator.validate(ukMinus, Locale.UK), "UK negative");
    assertEquals(noDecimal, validator.validate(ukPlus0Decimal, Locale.UK), "UK no decimal");
    assertEquals(oneDecimal, validator.validate(ukPlus1Decimal, Locale.UK), "UK 1 decimal");
    assertEquals(expected, validator.validate(ukPlus3Decimal, Locale.UK), "UK 3 decimal: " + ukPlus3Decimal);
    // TODO: The following expects the value to be truncated, rather than rounded up - is that correct?
    assertEquals(expected, validator.validate(ukPlus3Decimal, Locale.UK), "UK 3 decimal: " + ukPlus3Decimal);
    assertEquals(expected, validator.validate("1,234.56", Locale.UK), "UK no symbol");
    assertEquals(expected, validator.validate(usPlus, Locale.US), "US locale");
    assertEquals(negative, validator.validate(usMinus, Locale.US), "US negative");
    assertEquals(noDecimal, validator.validate(usPlus0Decimal, Locale.US), "US no decimal");
    assertEquals(oneDecimal, validator.validate(usPlus1Decimal, Locale.US), "US 1 decimal");
    // TODO: The following expects the value to be truncated, rather than rounded up - is that correct?
    assertEquals(expected, validator.validate(usPlus3Decimal, Locale.US), "US 3 decimal: " + usPlus3Decimal);
    assertEquals(expected, validator.validate("1,234.56", Locale.US), "US no symbol");
}

/**
 * Test compare date methods
 */
@Test
void testCompare() {
    final int sameTime = 124522;
    final int testDate = 20050823;
    // same date, different time
    final Date diffHour = createDate(TimeZones.GMT, testDate, 115922);
    // test value
    final Date value = createDate(TimeZones.GMT, testDate, sameTime);
    // +1 day
    final Date date20050824 = createDate(TimeZones.GMT, 20050824, sameTime);
    // -1 day
    final Date date20050822 = createDate(TimeZones.GMT, 20050822, sameTime);
    // +1 week
    final Date date20050830 = createDate(TimeZones.GMT, 20050830, sameTime);
    // -1 week
    final Date date20050816 = createDate(TimeZones.GMT, 20050816, sameTime);
    // +1 month
    final Date date20050901 = createDate(TimeZones.GMT, 20050901, sameTime);
    // same month
    final Date date20050801 = createDate(TimeZones.GMT, 20050801, sameTime);
    // -1 month
    final Date date20050731 = createDate(TimeZones.GMT, 20050731, sameTime);
    // +1 quarter (Feb Start)
    final Date date20051101 = createDate(TimeZones.GMT, 20051101, sameTime);
    // +1 quarter
    final Date date20051001 = createDate(TimeZones.GMT, 20051001, sameTime);
    // same quarter
    final Date date20050701 = createDate(TimeZones.GMT, 20050701, sameTime);
    // -1 quarter
    final Date date20050630 = createDate(TimeZones.GMT, 20050630, sameTime);
    // Previous Year qtr (Fen start)
    final Date date20050110 = createDate(TimeZones.GMT, 20050110, sameTime);
    // +1 year
    final Date date20060101 = createDate(TimeZones.GMT, 20060101, sameTime);
    // same year
    final Date date20050101 = createDate(TimeZones.GMT, 20050101, sameTime);
    // -1 year
    final Date date20041231 = createDate(TimeZones.GMT, 20041231, sameTime);
    // +1 day
    assertEquals(-1, dateValidator.compareDates(value, date20050824, TimeZones.GMT), "date LT");
    // same day, diff hour
    assertEquals(0, dateValidator.compareDates(value, diffHour, TimeZones.GMT), "date EQ");
    // -1 day
    assertEquals(1, dateValidator.compareDates(value, date20050822, TimeZones.GMT), "date GT");
    // +1 week
    assertEquals(-1, dateValidator.compareWeeks(value, date20050830, TimeZones.GMT), "week LT");
    // +1 day
    assertEquals(0, dateValidator.compareWeeks(value, date20050824, TimeZones.GMT), "week =1");
    // same week
    assertEquals(0, dateValidator.compareWeeks(value, date20050822, TimeZones.GMT), "week =2");
    // -1 day
    assertEquals(0, dateValidator.compareWeeks(value, date20050822, TimeZones.GMT), "week =3");
    // -1 week
    assertEquals(1, dateValidator.compareWeeks(value, date20050816, TimeZones.GMT), "week GT");
    // +1 month
    assertEquals(-1, dateValidator.compareMonths(value, date20050901, TimeZones.GMT), "mnth LT");
    // +1 week
    assertEquals(0, dateValidator.compareMonths(value, date20050830, TimeZones.GMT), "mnth =1");
    // same month
    assertEquals(0, dateValidator.compareMonths(value, date20050801, TimeZones.GMT), "mnth =2");
    // -1 week
    assertEquals(0, dateValidator.compareMonths(value, date20050816, TimeZones.GMT), "mnth =3");
    // -1 month
    assertEquals(1, dateValidator.compareMonths(value, date20050731, TimeZones.GMT), "mnth GT");
    // +1 quarter (Feb)
    assertEquals(-1, dateValidator.compareQuarters(value, date20051101, TimeZones.GMT), "qtrA <1");
    // +1 quarter
    assertEquals(-1, dateValidator.compareQuarters(value, date20051001, TimeZones.GMT), "qtrA <2");
    // +1 month
    assertEquals(0, dateValidator.compareQuarters(value, date20050901, TimeZones.GMT), "qtrA =1");
    // same quarter
    assertEquals(0, dateValidator.compareQuarters(value, date20050701, TimeZones.GMT), "qtrA =2");
    // -1 month
    assertEquals(0, dateValidator.compareQuarters(value, date20050731, TimeZones.GMT), "qtrA =3");
    // -1 quarter
    assertEquals(1, dateValidator.compareQuarters(value, date20050630, TimeZones.GMT), "qtrA GT");
    // Change quarter 1 to start in Feb
    // +1 quarter (Feb)
    assertEquals(-1, dateValidator.compareQuarters(value, date20051101, TimeZones.GMT, 2), "qtrB LT");
    // same quarter
    assertEquals(0, dateValidator.compareQuarters(value, date20051001, TimeZones.GMT, 2), "qtrB =1");
    // +1 month
    assertEquals(0, dateValidator.compareQuarters(value, date20050901, TimeZones.GMT, 2), "qtrB =2");
    // same quarter
    assertEquals(1, dateValidator.compareQuarters(value, date20050701, TimeZones.GMT, 2), "qtrB =3");
    // -1 month
    assertEquals(1, dateValidator.compareQuarters(value, date20050731, TimeZones.GMT, 2), "qtrB =4");
    // -1 quarter
    assertEquals(1, dateValidator.compareQuarters(value, date20050630, TimeZones.GMT, 2), "qtrB GT");
    // Jan Prev year qtr
    assertEquals(1, dateValidator.compareQuarters(value, date20050110, TimeZones.GMT, 2), "qtrB prev");
    // +1 year
    assertEquals(-1, dateValidator.compareYears(value, date20060101, TimeZones.GMT), "year LT");
    // same year
    assertEquals(0, dateValidator.compareYears(value, date20050101, TimeZones.GMT), "year EQ");
    // -1 year
    assertEquals(1, dateValidator.compareYears(value, date20041231, TimeZones.GMT), "year GT");
    // Compare using alternative TimeZone
    final Date sameDayTwoAm = createDate(TimeZones.GMT, testDate, 20000);
    // +1 day
    assertEquals(-1, dateValidator.compareDates(value, date20050824, TestTimeZones.EST), "date LT");
    // same day, diff hour
    assertEquals(0, dateValidator.compareDates(value, diffHour, TestTimeZones.EST), "date EQ");
    // same day, diff hour
    assertEquals(1, dateValidator.compareDates(value, sameDayTwoAm, TestTimeZones.EST), "date EQ");
    // -1 day
    assertEquals(1, dateValidator.compareDates(value, date20050822, TestTimeZones.EST), "date GT");
}

