/**
 * Test compare date methods
 */
@Test
void testCompare() {
    final int testTime = 154523;
    final int min = 100;
    final int hour = 10000;
    // > milli sec
    final Calendar milliGreater = createTime(TimeZones.GMT, testTime, 500);
    // test value
    final Calendar value = createTime(TimeZones.GMT, testTime, 400);
    // < milli sec
    final Calendar milliLess = createTime(TimeZones.GMT, testTime, 300);
    // +1 sec
    final Calendar secGreater = createTime(TimeZones.GMT, testTime + 1, 100);
    // -1 sec
    final Calendar secLess = createTime(TimeZones.GMT, testTime - 1, 100);
    // +1 min
    final Calendar minGreater = createTime(TimeZones.GMT, testTime + min, 100);
    // -1 min
    final Calendar minLess = createTime(TimeZones.GMT, testTime - min, 100);
    // +1 hour
    final Calendar hourGreater = createTime(TimeZones.GMT, testTime + hour, 100);
    // -1 hour
    final Calendar hourLess = createTime(TimeZones.GMT, testTime - hour, 100);
    // > milli
    assertEquals(-1, validator.compareTime(value, milliGreater), "milli LT");
    // same time
    assertEquals(0, validator.compareTime(value, value), "milli EQ");
    // < milli
    assertEquals(1, validator.compareTime(value, milliLess), "milli GT");
    // +1 sec
    assertEquals(-1, validator.compareSeconds(value, secGreater), "secs LT");
    // > milli
    assertEquals(0, validator.compareSeconds(value, milliGreater), "secs =1");
    // same time
    assertEquals(0, validator.compareSeconds(value, value), "secs =2");
    // < milli
    assertEquals(0, validator.compareSeconds(value, milliLess), "secs =3");
    // -1 sec
    assertEquals(1, validator.compareSeconds(value, secLess), "secs GT");
    // +1 min
    assertEquals(-1, validator.compareMinutes(value, minGreater), "mins LT");
    // +1 sec
    assertEquals(0, validator.compareMinutes(value, secGreater), "mins =1");
    // same time
    assertEquals(0, validator.compareMinutes(value, value), "mins =2");
    // -1 sec
    assertEquals(0, validator.compareMinutes(value, secLess), "mins =3");
    // -1 min
    assertEquals(1, validator.compareMinutes(value, minLess), "mins GT");
    // +1 hour
    assertEquals(-1, validator.compareHours(value, hourGreater), "hour LT");
    // +1 min
    assertEquals(0, validator.compareHours(value, minGreater), "hour =1");
    // same time
    assertEquals(0, validator.compareHours(value, value), "hour =2");
    // -1 min
    assertEquals(0, validator.compareHours(value, minLess), "hour =3");
    // -1 hour
    assertEquals(1, validator.compareHours(value, hourLess), "hour GT");
}

