    }
    final String message = String.format("Extensions: %s; files: %s", extensionSet.toString(), Arrays.asList(foArray).toString());
    assertEquals(EXTENSION_COUNT, extensionSet.size(), message);
    // check each extension
    for (final String extension : extensionSet) {
