@Test
public void testEqualsHashCodeAndCompareToWithSftpIdentityProviderMismatch() {
    final String pubKey1 = "/tmp/test.pub";
    final String pubKey2 = "/tmp/test1.pub";
    final String privKey1 = "/tmp/test.priv";
    final String privKey2 = "/tmp/test1.priv";
    assertSftpOptionsNotEquals(new File(privKey1), new File(pubKey1), new byte[] { 1, 2, 3 }, new File(privKey2), new File(pubKey1), new byte[] { 1, 2, 3 });
    assertSftpOptionsNotEquals(new File(privKey1), new File(pubKey1), new byte[] { 1, 2, 3 }, new File(privKey1), new File(pubKey2), new byte[] { 1, 2, 3 });
    assertSftpOptionsNotEquals(new File(privKey1), new File(pubKey1), new byte[] { 1, 2, 3 }, new File(privKey1), new File(pubKey1), new byte[] { 1, 2, 4 });
}

