/**
 * Sets a file system's file cache to use WeakReference, and test resolve file after GC.
 */
@Test
public void testZipFileUseWeakRefFilesCache() throws FileSystemException {
    final File file = new File("src/test/resources/test-data/test.zip");
    final String fileUri = "zip:file:" + file.getAbsolutePath();
    FileObject fileObject = null;
    try (StandardFileSystemManager manager = new StandardFileSystemManager()) {
        // set file system's file cache use WeakReference, and init file system
        // Managed by the file system, no need to close.
        @SuppressWarnings("resource")
        final WeakRefFilesCache filesCache = new WeakRefFilesCache();
        manager.setFilesCache(filesCache);
        manager.init();
        int cnt = 0;
        while (cnt < 100_000) {
            cnt++;
            // resolve file, assert fileObject exist. clear fileObject to null and wait GC.
            try {
                fileObject = manager.resolveFile(fileUri);
                assertTrue(fileObject.exists());
            } finally {
                FileObject.close(fileObject);
                fileObject = null;
            }
            // every 200 times suggest one gc
            if (cnt % 200 == 0) {
                System.gc();
            }
        }
    }
}

/**
 * Test the manual cache strategy.
 */
@Test
public void testManualCache() throws Exception {
    final FileObject scratchFolder = getWriteFolder();
    if (FileObjectUtils.isInstanceOf(getBaseFolder(), RamFileObject.class) || scratchFolder.getFileSystem() instanceof VirtualFileSystem) {
        // can't check ram filesystem as every manager holds its own ram filesystem data
        return;
    }
    scratchFolder.delete(Selectors.EXCLUDE_SELF);
    final DefaultFileSystemManager fs = createManager();
    fs.setCacheStrategy(CacheStrategy.MANUAL);
    fs.init();
    final FileObject foBase2 = getBaseTestFolder(fs);
    final FileObject cachedFolder = foBase2.resolveFile(scratchFolder.getName().getPath());
    FileObject[] fos = cachedFolder.getChildren();
    assertContainsNot(fos, "file1.txt");
    scratchFolder.resolveFile("file1.txt").createFile();
    fos = cachedFolder.getChildren();
    assertContainsNot(fos, "file1.txt");
    cachedFolder.refresh();
    fos = cachedFolder.getChildren();
    assertContains(fos, "file1.txt");
}

