/**
 * Read a file.
 */
@Test
public void testRandomRead() throws Exception {
    try (FileObject file = getReadFolder().resolveFile("file1.txt")) {
        final RandomAccessContent ra = file.getContent().getRandomAccessContent(RandomAccessMode.READ);
        // read first byte
        byte c = ra.readByte();
        assertEquals(TEST_DATA.charAt(0), c);
        assertEquals("fp", 1, ra.getFilePointer());
        // start at pos 4
        ra.seek(3);
        c = ra.readByte();
        assertEquals(TEST_DATA.charAt(3), c);
        assertEquals("fp", 4, ra.getFilePointer());
        c = ra.readByte();
        assertEquals(TEST_DATA.charAt(4), c);
        assertEquals("fp", 5, ra.getFilePointer());
        // restart at pos 4
        ra.seek(3);
        c = ra.readByte();
        assertEquals(TEST_DATA.charAt(3), c);
        assertEquals("fp", 4, ra.getFilePointer());
        c = ra.readByte();
        assertEquals(TEST_DATA.charAt(4), c);
        assertEquals("fp", 5, ra.getFilePointer());
        // advance to pos 11
        ra.seek(10);
        c = ra.readByte();
        assertEquals(TEST_DATA.charAt(10), c);
        assertEquals("fp", 11, ra.getFilePointer());
        c = ra.readByte();
        assertEquals(TEST_DATA.charAt(11), c);
        assertEquals("fp", 12, ra.getFilePointer());
    }
}

/**
 * Writes a file.
 */
@Test
public void testRandomWrite() throws Exception {
    try (FileObject file = createScratchFolder().resolveFile("random_write.txt")) {
        file.createFile();
        final RandomAccessContent ra = file.getContent().getRandomAccessContent(RandomAccessMode.READWRITE);
        // write first byte
        ra.writeByte(TEST_DATA.charAt(0));
        // start at pos 4
        ra.seek(3);
        ra.writeByte(TEST_DATA.charAt(3));
        ra.writeByte(TEST_DATA.charAt(4));
        // restart at pos 4 (but overwrite with different content)
        ra.seek(3);
        ra.writeByte(TEST_DATA.charAt(7));
        ra.writeByte(TEST_DATA.charAt(8));
        // advance to pos 11
        ra.seek(10);
        ra.writeByte(TEST_DATA.charAt(10));
        ra.writeByte(TEST_DATA.charAt(11));
        // now read
        ra.seek(0);
        assertEquals(ra.readByte(), TEST_DATA.charAt(0));
        ra.seek(3);
        assertEquals(ra.readByte(), TEST_DATA.charAt(7));
        assertEquals(ra.readByte(), TEST_DATA.charAt(8));
        ra.seek(10);
        assertEquals(ra.readByte(), TEST_DATA.charAt(10));
        assertEquals(ra.readByte(), TEST_DATA.charAt(11));
    }
}

/**
 * Writes a file.
 */
@Test
public void testRandomSetLength() throws Exception {
    try (FileObject file = createScratchFolder().resolveFile("random_write.txt")) {
        file.createFile();
        final String fileString = file.toString();
        final RandomAccessContent ra = file.getContent().getRandomAccessContent(RandomAccessMode.READWRITE);
        // Write long string
        ra.writeBytes(TEST_DATA);
        Assertions.assertEquals(TEST_DATA.length(), ra.length(), fileString);
        // Shrink to length 1
        ra.setLength(1);
        Assertions.assertEquals(1, ra.length(), fileString);
        // now read 1
        ra.seek(0);
        Assertions.assertEquals(TEST_DATA.charAt(0), ra.readByte(), fileString);
        try {
            ra.readByte();
            Assertions.fail("Expected " + Exception.class.getName());
        } catch (final IOException e) {
            // Expected
        }
        // Grow to length 2
        ra.setLength(2);
        Assertions.assertEquals(2, ra.length(), fileString);
        // We have an undefined extra byte
        ra.seek(1);
        ra.readByte();
    }
}

