@Test
public void uploadedFilesToDisk() throws IOException {
    // given
    String content = formFile("file1", "test1.csv", "1,2,3,4") + formFile("file2", "test2.csv", "5,6,7,8") + endline + "--" + boundary + "--";
    mockRequest.setContent(content.getBytes(StandardCharsets.UTF_8));
    assertThat(JakartaServletDiskFileUpload.isMultipartContent(mockRequest)).isTrue();
    // when
    // always write files into disk
    multiPart.setBufferSize("1");
    multiPart.parse(mockRequest, tempDir);
    // then
    assertThat(multiPart.getErrors()).isEmpty();
    assertThat(multiPart.getFileParameterNames().asIterator()).toIterable().asInstanceOf(InstanceOfAssertFactories.LIST).containsOnly("file1", "file2");
    assertThat(multiPart.getFile("file1")).allSatisfy(file -> {
        assertThat(file.isFile()).isTrue();
        assertThat(file.getOriginalName()).isEqualTo("test1.csv");
        assertThat(file.getContentType()).isEqualTo("text/csv");
        assertThat(file.getInputName()).isEqualTo("file1");
        assertThat(file.getContent()).asInstanceOf(InstanceOfAssertFactories.FILE).exists().content().isEqualTo("1,2,3,4");
    });
    assertThat(multiPart.getFile("file2")).allSatisfy(file -> {
        assertThat(file.isFile()).isTrue();
        assertThat(file.getOriginalName()).isEqualTo("test2.csv");
