    String content = endline + "--" + boundary + endline + "Content-Disposition: form-data; name=\"emptyfile1\"; filename=\"empty1.txt\"" + endline + "Content-Type: text/plain" + endline + endline + // No content - empty file
    endline + "--" + boundary + endline + "Content-Disposition: form-data; name=\"validfile\"; filename=\"valid.txt\"" + endline + "Content-Type: text/plain" + endline + endline + "some valid content" + endline + "--" + boundary + endline + "Content-Disposition: form-data; name=\"emptyfile2\"; filename=\"empty2.txt\"" + endline + "Content-Type: application/octet-stream" + endline + endline + // Another empty file
    endline + "--" + boundary + "--";
