    JSONWriter jsonWriter = new DefaultJSONWriter();
    jsonWriter.setEnumAsBean(false);
    jsonWriter.setDateFormatter("MM-dd-yyyy");
    String json = jsonWriter.write(dateBean);
    assertEquals("{\"date\":\"12-23-2012\"}", json);
}

