@Test
public void testSaveAndLoad() throws Exception {
    List<String> list = new ArrayList<>();
    list.add("quick");
    list.add("brown");
    list.add("fox");
    Object[] objects = new Object[] { list, Integer.MAX_VALUE, 2.5f, 4000.57576, true, false, Long.MAX_VALUE, "Hello this is a boring string", new URI("http://apache.org").toURL(), new URI("tika://org.apache.tika.ner.parser?impl=xyz"), new BigInteger(Long.MAX_VALUE + "").add(new BigInteger(Long.MAX_VALUE + "")), new File(".") };
    for (Object object : objects) {
        String name = "name" + System.currentTimeMillis();
        Param<?> param = new Param<>(name, object);
        ByteArrayOutputStream stream = new ByteArrayOutputStream();
        param.save(stream);
        ByteArrayInputStream inStream = new ByteArrayInputStream(stream.toByteArray());
        stream.close();
        inStream.close();
        Param<?> loaded = Param.load(inStream);
        assertEquals(param.getName(), loaded.getName());
        assertEquals(param.getTypeString(), loaded.getTypeString());
        assertEquals(param.getType(), loaded.getType());
        assertEquals(param.getValue(), loaded.getValue());
        assertEquals(loaded.getValue(), object);
        assertEquals(loaded.getName(), name);
        assertEquals(loaded.getType(), object.getClass());
    }
}

