@Test
public void testFileBased() throws IOException {
    Path path = createTempFile("Hello, World!");
    TikaInputStream stream = TikaInputStream.get(path);
    assertTrue(stream.hasFile());
    assertNull(stream.getOpenContainer());
    assertNull(stream.getInputStreamFactory());
    assertEquals(path, TikaInputStream.get(stream).getPath(), "The file returned by the getFile() method should" + " be the file used to instantiate a TikaInputStream");
    assertEquals("Hello, World!", readStream(stream), "The contents of the TikaInputStream should equal the" + " contents of the underlying file");
    stream.close();
    assertTrue(Files.exists(path), "The close() method must not remove the file used to" + " instantiate a TikaInputStream");
}

@Test
public void testStreamBased() throws IOException {
    InputStream input = IOUtils.toInputStream("Hello, World!", UTF_8);
    TikaInputStream stream = TikaInputStream.get(input);
    assertFalse(stream.hasFile());
    assertNull(stream.getOpenContainer());
    assertNull(stream.getInputStreamFactory());
    Path file = TikaInputStream.get(stream).getPath();
    assertTrue(file != null && Files.isRegularFile(file));
    assertTrue(stream.hasFile());
    assertNull(stream.getOpenContainer());
    assertNull(stream.getInputStreamFactory());
    assertEquals("Hello, World!", readFile(file), "The contents of the file returned by the getFile method" + " should equal the contents of the TikaInputStream");
    assertEquals("Hello, World!", readStream(stream), "The contents of the TikaInputStream should not get modified" + " by reading the file first");
    stream.close();
    assertFalse(Files.exists(file), "The close() method must remove the temporary file created by a TikaInputStream");
}

@Test
public void testInputStreamFactoryBased() throws IOException {
