    EmbeddedDocumentBytesConfig embeddedDocumentBytesConfig = new EmbeddedDocumentBytesConfig(true);
    embeddedDocumentBytesConfig.setIncludeOriginal(true);
    ParseContext parseContext = new ParseContext();
    parseContext.set(HandlerConfig.class, HandlerConfig.DEFAULT_HANDLER_CONFIG);
    parseContext.set(EmbeddedDocumentBytesConfig.class, embeddedDocumentBytesConfig);
    FetchEmitTuple fetchEmitTuple = new FetchEmitTuple("id", new FetchKey("fs", "mock.xml"), new EmitKey("", ""), new Metadata(), parseContext);
    Fetcher fetcher = FetcherManager.load(tikaConfig).getFetcher();
    PipesServer.MetadataListAndEmbeddedBytes parseData = pipesServer.parseFromTuple(fetchEmitTuple, fetcher);
    assertEquals(2, parseData.metadataList.size());
    byte[] bytes0 = IOUtils.toByteArray(((BasicEmbeddedDocumentBytesHandler) parseData.getEmbeddedDocumentBytesHandler()).getDocument(0));
    byte[] bytes1 = IOUtils.toByteArray(((BasicEmbeddedDocumentBytesHandler) parseData.getEmbeddedDocumentBytesHandler()).getDocument(1));
    assertContains("is to trigger mock on the embedded", new String(bytes0, StandardCharsets.UTF_8));
    assertEquals(10, bytes1.length);
    assertEquals("fdaa937c96d1ed010b8d307ccddf9d11c3b48db732a8771eaafe99d59e076d0a", parseData.metadataList.get(0).get("X-TIKA:digest:SHA-256"));
}

