    xhtml.element("td", "a");
    xhtml.element("td", "b");
    xhtml.endElement("tr");
    xhtml.endElement("table");
    xhtml.endDocument();
    String[] words = output.toString().split("\\s+");
    assertEquals(6, words.length);
    assertEquals("foo", words[0]);
