@Test
public void testFetcherCrud(Resources resources) throws Exception {
    Assertions.assertTrue(tikaConfigXml.setWritable(false));
    String serverName = InProcessServerBuilder.generateName();
    Server server = InProcessServerBuilder.forName(serverName).directExecutor().addService(new TikaGrpcServerImpl(tikaConfigXml.getAbsolutePath())).build().start();
    resources.register(server, Duration.ofSeconds(10));
    ManagedChannel channel = InProcessChannelBuilder.forName(serverName).directExecutor().build();
    resources.register(channel, Duration.ofSeconds(10));
    TikaGrpc.TikaBlockingStub blockingStub = TikaGrpc.newBlockingStub(channel);
    String targetFolder = new File("target").getAbsolutePath();
    // create fetchers
    for (int i = 0; i < NUM_FETCHERS_TO_CREATE; ++i) {
        String fetcherId = createFetcherId(i);
        SaveFetcherReply reply = blockingStub.saveFetcher(SaveFetcherRequest.newBuilder().setFetcherId(fetcherId).setFetcherClass(FileSystemFetcher.class.getName()).setFetcherConfigJson(OBJECT_MAPPER.writeValueAsString(ImmutableMap.builder().put("basePath", targetFolder).put("extractFileSystemMetadata", true).build())).build());
        assertEquals(fetcherId, reply.getFetcherId());
    }
    // update fetchers
    for (int i = 0; i < NUM_FETCHERS_TO_CREATE; ++i) {
        String fetcherId = createFetcherId(i);
        SaveFetcherReply reply = blockingStub.saveFetcher(SaveFetcherRequest.newBuilder().setFetcherId(fetcherId).setFetcherClass(FileSystemFetcher.class.getName()).setFetcherConfigJson(OBJECT_MAPPER.writeValueAsString(ImmutableMap.builder().put("basePath", targetFolder).put("extractFileSystemMetadata", false).build())).build());
        assertEquals(fetcherId, reply.getFetcherId());
        GetFetcherReply getFetcherReply = blockingStub.getFetcher(GetFetcherRequest.newBuilder().setFetcherId(fetcherId).build());
        assertEquals("false", getFetcherReply.getParamsMap().get("extractFileSystemMetadata"));
    }
    // get fetchers
    for (int i = 0; i < NUM_FETCHERS_TO_CREATE; ++i) {
        String fetcherId = createFetcherId(i);
        GetFetcherReply getFetcherReply = blockingStub.getFetcher(GetFetcherRequest.newBuilder().setFetcherId(fetcherId).build());
        assertEquals(fetcherId, getFetcherReply.getFetcherId());
        assertEquals(FileSystemFetcher.class.getName(), getFetcherReply.getFetcherClass());
    }
    // delete fetchers
    for (int i = 0; i < NUM_FETCHERS_TO_CREATE; ++i) {
        String fetcherId = createFetcherId(i);
        DeleteFetcherReply deleteFetcherReply = blockingStub.deleteFetcher(DeleteFetcherRequest.newBuilder().setFetcherId(fetcherId).build());
        Assertions.assertTrue(deleteFetcherReply.getSuccess());
        StatusRuntimeException statusRuntimeException = Assertions.assertThrows(StatusRuntimeException.class, () -> blockingStub.getFetcher(GetFetcherRequest.newBuilder().setFetcherId(fetcherId).build()));
        Assertions.assertEquals(Status.NOT_FOUND.getCode().value(), statusRuntimeException.getStatus().getCode().value());
    }
}

@Test
public void testBiStream(Resources resources) throws Exception {
    String serverName = InProcessServerBuilder.generateName();
    Server server = InProcessServerBuilder.forName(serverName).directExecutor().addService(new TikaGrpcServerImpl(tikaConfigXml.getAbsolutePath())).build().start();
    resources.register(server, Duration.ofSeconds(10));
    ManagedChannel channel = InProcessChannelBuilder.forName(serverName).directExecutor().build();
    resources.register(channel, Duration.ofSeconds(10));
    TikaGrpc.TikaBlockingStub blockingStub = TikaGrpc.newBlockingStub(channel);
    TikaGrpc.TikaStub tikaStub = TikaGrpc.newStub(channel);
    String fetcherId = createFetcherId(1);
    String targetFolder = new File("target").getAbsolutePath();
    SaveFetcherReply reply = blockingStub.saveFetcher(SaveFetcherRequest.newBuilder().setFetcherId(fetcherId).setFetcherClass(FileSystemFetcher.class.getName()).setFetcherConfigJson(OBJECT_MAPPER.writeValueAsString(ImmutableMap.builder().put("basePath", targetFolder).put("extractFileSystemMetadata", true).build())).build());
    assertEquals(fetcherId, reply.getFetcherId());
    List<FetchAndParseReply> successes = Collections.synchronizedList(new ArrayList<>());
    List<FetchAndParseReply> errors = Collections.synchronizedList(new ArrayList<>());
    AtomicBoolean finished = new AtomicBoolean(false);
    StreamObserver<FetchAndParseReply> replyStreamObserver = new StreamObserver<>() {

        @Override
        public void onNext(FetchAndParseReply fetchAndParseReply) {
            LOG.debug("Fetched {} with metadata {}", fetchAndParseReply.getFetchKey(), fetchAndParseReply.getFieldsMap());
            if (PipesResult.STATUS.FETCH_EXCEPTION.name().equals(fetchAndParseReply.getStatus())) {
                errors.add(fetchAndParseReply);
            } else {
                successes.add(fetchAndParseReply);
            }
        }

        @Override
        public void onError(Throwable throwable) {
            fail(throwable);
        }

        @Override
        public void onCompleted() {
            LOG.info("Stream completed");
            finished.set(true);
        }
    };
    StreamObserver<FetchAndParseRequest> requestStreamObserver = tikaStub.fetchAndParseBiDirectionalStreaming(replyStreamObserver);
    File testDocumentFolder = new File("target/" + DateTimeFormatter.ofPattern("yyyy_MM_dd_HH_mm_ssSSS", Locale.getDefault()).format(LocalDateTime.now(ZoneId.systemDefault())) + "-" + UUID.randomUUID());
    assertTrue(testDocumentFolder.mkdir());
    try {
        for (int i = 0; i < NUM_TEST_DOCS; ++i) {
            File testFile = new File(testDocumentFolder, "test-" + i + ".html");
            FileUtils.writeStringToFile(testFile, "<html><body>test " + i + "</body></html>", StandardCharsets.UTF_8);
        }
        File[] testDocuments = testDocumentFolder.listFiles();
        assertNotNull(testDocuments);
        for (File testDocument : testDocuments) {
            requestStreamObserver.onNext(FetchAndParseRequest.newBuilder().setFetcherId(fetcherId).setFetchKey(testDocument.getAbsolutePath()).build());
        }
        // Now test error condition
        requestStreamObserver.onNext(FetchAndParseRequest.newBuilder().setFetcherId(fetcherId).setFetchKey("does not exist").build());
        requestStreamObserver.onCompleted();
        assertEquals(NUM_TEST_DOCS, successes.size());
        assertEquals(1, errors.size());
        assertTrue(finished.get());
    } finally {
        FileUtils.deleteDirectory(testDocumentFolder);
    }
}

@Test
public void testKafkaPipeIteratorAndEmitter() throws Exception {
    createTestFiles();
    File tikaConfigFile = new File("target", "ta.xml");
    File log4jPropFile = new File("target", "tmp-log4j2.xml");
    try (InputStream is = this.getClass().getResourceAsStream("/pipes-fork-server-custom-log4j2.xml")) {
        assert is != null;
        FileUtils.copyInputStreamToFile(is, log4jPropFile);
    }
    String tikaConfigTemplateXml;
    try (InputStream is = this.getClass().getResourceAsStream("/tika-config-kafka.xml")) {
        assert is != null;
        tikaConfigTemplateXml = IOUtils.toString(is, StandardCharsets.UTF_8);
    }
    Properties consumerProps = new Properties();
    consumerProps.put(ConsumerConfig.BOOTSTRAP_SERVERS_CONFIG, kafka.getBootstrapServers());
    consumerProps.put("group.id", UUID.randomUUID().toString());
    consumerProps.put(ConsumerConfig.KEY_DESERIALIZER_CLASS_CONFIG, StringDeserializer.class.getName());
    consumerProps.put(ConsumerConfig.VALUE_DESERIALIZER_CLASS_CONFIG, StringDeserializer.class.getName());
    consumerProps.put(ConsumerConfig.AUTO_OFFSET_RESET_CONFIG, "earliest");
    Properties producerProps = new Properties();
    producerProps.put(ProducerConfig.BOOTSTRAP_SERVERS_CONFIG, kafka.getBootstrapServers());
    producerProps.put(ProducerConfig.KEY_SERIALIZER_CLASS_CONFIG, StringSerializer.class.getName());
    producerProps.put(ProducerConfig.VALUE_SERIALIZER_CLASS_CONFIG, StringSerializer.class.getName());
    KafkaConsumer<String, String> consumer = new KafkaConsumer<>(consumerProps);
    LOG.info("Listening to EMITTER_TOPIC={}", EMITTER_TOPIC);
    consumer.subscribe(Collections.singletonList(EMITTER_TOPIC));
    ExecutorService es = Executors.newCachedThreadPool();
    try (KafkaProducer<String, String> producer = new KafkaProducer<>(producerProps)) {
        int numSent = 0;
        for (int i = 0; i < numDocs; ++i) {
            File nextFile = new File(testFileFolder, "test-" + i + ".html");
            Map<String, Object> meta = new HashMap<>();
            meta.put("name", nextFile.getName());
            meta.put("path", nextFile.getAbsolutePath());
            meta.put("totalSpace", nextFile.getTotalSpace());
            try {
                producer.send(new ProducerRecord<>(PIPE_ITERATOR_TOPIC, nextFile.getAbsolutePath(), objectMapper.writeValueAsString(meta))).get();
                LOG.info("Sent fetch request : {}", nextFile.getAbsolutePath());
                ++numSent;
            } catch (Exception e) {
                throw new RuntimeException(e);
            }
        }
        LOG.info("Producer is now complete - sent {}.", numSent);
    }
    es.execute(() -> {
        try {
            String tikaConfigXml = createTikaConfigXml(tikaConfigFile, log4jPropFile, tikaConfigTemplateXml);
            FileUtils.writeStringToFile(tikaConfigFile, tikaConfigXml, StandardCharsets.UTF_8);
            TikaCLI.main(new String[] { "-a", "--config=" + tikaConfigFile.getAbsolutePath() });
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    });
    LOG.info("Tika pipes have been started. See if we can pull the response messages from the EMITTER_TOPIC={}", EMITTER_TOPIC);
    Stopwatch stopwatch = Stopwatch.createStarted();
    while (!waitingFor.isEmpty()) {
        assertFalse(stopwatch.elapsed(TimeUnit.MINUTES) > WAIT_FOR_EMITTED_DOCS_TIMEOUT_MINUTES, "Timed out after " + WAIT_FOR_EMITTED_DOCS_TIMEOUT_MINUTES + " minutes waiting for the emitted docs");
        try {
            ConsumerRecords<String, String> records = consumer.poll(Duration.ofSeconds(1));
            for (ConsumerRecord<String, String> record : records) {
                String val = record.value();
                Map<String, Object> valMap = objectMapper.readValue(val, new TypeReference<Map<String, Object>>() {
                });
                waitingFor.remove(FilenameUtils.getName(record.key()));
                assertNotNull(valMap.get("content_s"));
                assertNotNull(valMap.get("mime_s"));
                assertNotNull(valMap.get("length_i"));
                LOG.info("Received message key={}, offset={}", record.key(), record.offset());
            }
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }
    LOG.info("Done");
}

@Test
public void testBasicFSToOpenSearch(@TempDir Path pipesDirectory, @TempDir Path testDocDirectory) throws Exception {
    OpensearchTestClient client = getNewClient();
    int numHtmlDocs = 42;
    createTestHtmlFiles("Happiness", numHtmlDocs, testDocDirectory);
    String endpoint = CONTAINER.getHttpHostAddress() + "/" + TEST_INDEX;
    sendMappings(client, endpoint, TEST_INDEX, "opensearch-mappings.json");
    runPipes(client, OpenSearchEmitter.AttachmentStrategy.SEPARATE_DOCUMENTS, OpenSearchEmitter.UpdateStrategy.UPSERT, HandlerConfig.PARSE_MODE.CONCATENATE, endpoint, pipesDirectory, testDocDirectory);
    String query = "{ \"track_total_hits\": true, \"query\": { \"match\": { \"content\": { " + "\"query\": \"happiness\" } } } }";
    JsonResponse results = client.postJson(endpoint + "/_search", query);
    assertEquals(200, results.getStatus());
    assertEquals(numHtmlDocs + 1, results.getJson().get("hits").get("total").get("value").asInt());
    //now try match all
    query = "{ \"track_total_hits\": true, \"query\": { \"match_all\": {} }, " + "\"from\": 0, \"size\": 1000 }";
    results = client.postJson(endpoint + "/_search", query);
    assertEquals(200, results.getStatus());
    assertEquals(numHtmlDocs + numTestDocs, results.getJson().get("hits").get("total").get("value").asInt());
    //now test that the reporter worked
    Map<String, Integer> statusCounts = new HashMap<>();
    for (JsonNode n : results.getJson().get("hits").get("hits")) {
        String status = n.get("_source").get("my_test_parse_status").asText();
        //this will throw an NPE if the field isn't there
        //in short, this guarantees that the value is there
        long parseTimeMs = n.get("_source").get("my_test_parse_time_ms").asLong();
        Integer cnt = statusCounts.get(status);
        if (cnt == null) {
            cnt = 1;
        } else {
            cnt++;
        }
        statusCounts.put(status, cnt);
    }
    assertEquals(numHtmlDocs, (int) statusCounts.get("PARSE_SUCCESS"));
    //the npe is caught and counted as a "parse success with exception"
    assertEquals(1, (int) statusCounts.get("PARSE_SUCCESS_WITH_EXCEPTION"));
    //the embedded docx is emitted directly
    assertEquals(1, (int) statusCounts.get("EMIT_SUCCESS"));
    assertEquals(2, (int) statusCounts.get("OOM"));
}

