 */
@Test
public void testPowerPoint() throws Exception {
    String[] extensions = new String[] { "pptx", "pptm", "ppsm", "ppsx", "potm" //"thmx", // TIKA-418: Will be supported in POI 3.7 beta 2
    //"xps" // TIKA-418: Not yet supported by POI
    };
    String[] mimeTypes = new String[] { "application/vnd.openxmlformats-officedocument.presentationml.presentation", "application/vnd.ms-powerpoint.presentation.macroenabled.12", "application/vnd.ms-powerpoint.slideshow.macroenabled.12", "application/vnd.openxmlformats-officedocument.presentationml.slideshow", "application/vnd.ms-powerpoint.template.macroenabled.12" };
    for (int i = 0; i < extensions.length; i++) {
        String extension = extensions[i];
        String filename = "testPPT." + extension;
        Metadata metadata = new Metadata();
        ContentHandler handler = new BodyContentHandler();
        ParseContext context = new ParseContext();
        String content = getText(filename, metadata, context);
        assertEquals(mimeTypes[i], metadata.get(Metadata.CONTENT_TYPE), "Mime-type checking for " + filename);
        assertEquals("Attachment Test", metadata.get(TikaCoreProperties.TITLE));
        assertEquals("Rajiv", metadata.get(TikaCoreProperties.CREATOR));
        // Theme files don't have the text in them
        if (extension.equals("thmx")) {
            assertEquals("", content);
        } else {
            assertTrue(content.contains("Attachment Test"), "Text missing for " + filename + "\n" + content);
            assertTrue(content.contains("This is a test file data with the same content"), "Text missing for " + filename + "\n" + content);
            assertTrue(content.contains("content parsing"), "Text missing for " + filename + "\n" + content);
            assertTrue(content.contains("Different words to test against"), "Text missing for " + filename + "\n" + content);
            assertTrue(content.contains("Mystery"), "Text missing for " + filename + "\n" + content);
        }
    }
}

