    int m = xml.indexOf("Bye for real.");
    assertTrue(i != -1);
    assertTrue(j != -1);
    assertTrue(k != -1);
    assertTrue(l != -1);
    assertTrue(m != -1);
    assertTrue(i < j);
    assertTrue(j < k);
    assertTrue(k < l);
    assertTrue(l < m);
}

