    Metadata m = new Metadata();
    m.add(TikaCoreProperties.RESOURCE_NAME_KEY, "excel.xlsb");
    // Should be detected correctly
    MediaType type;
    try (InputStream input = getResourceAsStream("/test-documents/testEXCEL.xlsb")) {
