@Test
public void testMultiThreaded() throws Exception {
    int numThreads = 10;
    ExecutorService executorService = Executors.newFixedThreadPool(numThreads);
    ExecutorCompletionService<Integer> executorCompletionService = new ExecutorCompletionService<>(executorService);
    for (int i = 0; i < numThreads; i++) {
        executorCompletionService.submit(() -> {
            for (int i1 = 0; i1 < 10; i1++) {
                List<Metadata> metadataList = getRecursiveMetadata("testODTEmbedded.odt");
                assertEquals(3, metadataList.size());
                assertEquals("THUMBNAIL", metadataList.get(1).get(TikaCoreProperties.EMBEDDED_RESOURCE_TYPE));
            }
            return 1;
        });
    }
    try {
        int finished = 0;
        while (finished < numThreads) {
            Future<Integer> future = executorCompletionService.take();
            future.get();
            finished++;
        }
    } finally {
        executorService.shutdownNow();
    }
}

@Test
public void testODTXHTMLIsParseable() throws Exception {
    //for all OpenDocument files, make sure that the
    //output from the parse is parseable xhtml
    int filesTested = 0;
    for (Path p : getAllTestFiles()) {
        String fileName = p.getFileName().toString();
        if (fileName.endsWith(".odt") || fileName.endsWith("odp") || fileName.endsWith("odf") || fileName.endsWith(".ods")) {
            XMLResult xmlResult = null;
            try (InputStream is = TikaInputStream.get(p)) {
                xmlResult = getXML(is, AUTO_DETECT_PARSER, new Metadata());
            } catch (Exception e) {
                continue;
            }
            try {
                //just make sure this doesn't throw any exceptions
                XMLReaderUtils.parseSAX(new ByteArrayInputStream(xmlResult.xml.getBytes(StandardCharsets.UTF_8)), new DefaultHandler(), new ParseContext());
                filesTested++;
            } catch (Exception e) {
                fail(p.getFileName().toString(), e);
            }
        }
    }
    assertTrue(filesTested > 10);
}

