@Test
public void testEmbeddedLists() throws Exception {
    Parser p = new OpenDocumentContentParser();
    Metadata metadata = new Metadata();
    XMLResult r = null;
    try (InputStream is = new GzipCompressorInputStream(getResourceAsStream("/test-documents/testODTBodyListOpenClose.xml.gz"))) {
        r = getXML(is, p, metadata);
    }
    String xml = r.xml;
    //extract all the tags
    Matcher m = Pattern.compile("(<[^>]+>)").matcher(xml);
    StringBuilder sb = new StringBuilder();
    while (m.find()) {
        sb.append(m.group(1)).append(" ");
    }
    String tags = sb.toString();
    assertContains("</p> <ol> <li> <p> </p> <ul> <li> <p> </p> </li> <li> <p> </p> </li> " + "</ul> </li> </ol>", tags);
    assertContains("Exercice", xml);
    m = Pattern.compile("<p class=\"annotation\">[^<]+<\\/p>").matcher(xml);
    assertTrue(m.find());
    m = Pattern.compile("<p class=\"annotation\">[^<]+<\\/annotation>").matcher(xml);
    assertFalse(m.find());
    //just make sure this doesn't throw any exceptions
    XMLReaderUtils.parseSAX(new ByteArrayInputStream(xml.getBytes(StandardCharsets.UTF_8)), new DefaultHandler(), new ParseContext());
}

