        ex = true;
    }
    assertTrue(ex, "can't add multiple values");
    m = new Metadata();
    ex = false;
    m.add(AccessPermissions.EXTRACT_FOR_ACCESSIBILITY, "true");
    try {
        m.add(AccessPermissions.EXTRACT_FOR_ACCESSIBILITY, "false");
