@Test
public void testAccessCheckingUserPassword() throws Exception {
    ParseContext context = new ParseContext();
    PDFParserConfig config = new PDFParserConfig();
    //don't allow extraction, not even for accessibility
    config.setAccessChecker(new AccessChecker(false));
    PasswordProvider passwordProvider = new PasswordProvider() {

        @Override
        public String getPassword(Metadata metadata) {
            return "user";
        }
    };
    context.set(PasswordProvider.class, passwordProvider);
    context.set(PDFParserConfig.class, config);
    //test bad passwords
    for (String path : new String[] { "testPDF_no_extract_no_accessibility_owner_empty.pdf", "testPDF_no_extract_yes_accessibility_owner_empty.pdf" }) {
        assertException("/test-documents/" + path, AUTO_DETECT_PARSER, context, EncryptedDocumentException.class);
    }
    //bad password is still a bad password
    config.setAccessChecker(new AccessChecker(true));
    for (String path : new String[] { "testPDF_no_extract_no_accessibility_owner_empty.pdf", "testPDF_no_extract_yes_accessibility_owner_empty.pdf" }) {
        assertException("/test-documents/" + path, AUTO_DETECT_PARSER, context, EncryptedDocumentException.class);
    }
    //now test documents that require this "user" password
    assertException("/test-documents/" + "testPDF_no_extract_no_accessibility_owner_user.pdf", AUTO_DETECT_PARSER, context, AccessPermissionException.class);
    assertContains("Hello World", getXML("testPDF_no_extract_yes_accessibility_owner_user.pdf", context).xml);
    config.setAccessChecker(new AccessChecker(false));
    for (String path : new String[] { "testPDF_no_extract_no_accessibility_owner_user.pdf", "testPDF_no_extract_yes_accessibility_owner_user.pdf" }) {
        assertException("/test-documents/" + path, AUTO_DETECT_PARSER, context, AccessPermissionException.class);
    }
}

@Test
public void testAccessCheckingOwnerPassword() throws Exception {
    ParseContext context = new ParseContext();
    PDFParserConfig config = new PDFParserConfig();
    //don't allow extraction, not even for accessibility
    config.setAccessChecker(new AccessChecker(true));
    PasswordProvider passwordProvider = new PasswordProvider() {

        @Override
        public String getPassword(Metadata metadata) {
            return "owner";
        }
    };
    context.set(PasswordProvider.class, passwordProvider);
    context.set(PDFParserConfig.class, config);
    //with owner's password, text can be extracted, no matter the
    // AccessibilityChecker's settings
    for (String path : new String[] { "testPDF_no_extract_no_accessibility_owner_user.pdf", "testPDF_no_extract_yes_accessibility_owner_user.pdf", "testPDF_no_extract_no_accessibility_owner_empty.pdf", "testPDF_no_extract_yes_accessibility_owner_empty.pdf" }) {
        assertContains("Hello World", getXML(path, context).xml);
    }
    //really, with owner's password, all extraction is allowed
    config.setAccessChecker(new AccessChecker(false));
    for (String path : new String[] { "testPDF_no_extract_no_accessibility_owner_user.pdf", "testPDF_no_extract_yes_accessibility_owner_user.pdf", "testPDF_no_extract_no_accessibility_owner_empty.pdf", "testPDF_no_extract_yes_accessibility_owner_empty.pdf" }) {
        assertContains("Hello World", getXML(path, context).xml);
    }
}

