@Test
public void testBasic() throws Exception {
    EncodingDetector detector = new BOMDetector();
    for (ByteOrderMark bom : new ByteOrderMark[] { ByteOrderMark.UTF_8, ByteOrderMark.UTF_16BE, ByteOrderMark.UTF_16LE, ByteOrderMark.UTF_32BE, ByteOrderMark.UTF_32LE }) {
        UnsynchronizedByteArrayOutputStream bos = createStream(bom);
        try (BOMInputStream bomInputStream = BOMInputStream.builder().setInputStream(UnsynchronizedByteArrayInputStream.builder().setByteArray(bos.toByteArray()).get()).setByteOrderMarks(ByteOrderMark.UTF_8, ByteOrderMark.UTF_32BE, ByteOrderMark.UTF_32LE, ByteOrderMark.UTF_16BE, ByteOrderMark.UTF_16LE).get()) {
            assertEquals(bom, bomInputStream.getBOM());
        }
        try (UnsynchronizedByteArrayInputStream is = UnsynchronizedByteArrayInputStream.builder().setByteArray(bos.toByteArray()).get()) {
            assertEquals(Charset.forName(bom.getCharsetName()), detector.detect(is, new Metadata()));
            int cnt = 0;
            int c = is.read();
            while (c > -1) {
                cnt++;
                c = is.read();
            }
            assertEquals(100 + bom.getBytes().length, cnt);
        }
    }
}

@Test
public void testShort() throws Exception {
