//TIKA-1010 test regular (not "embedded") images/picts
@Test
public void testRegularImages() throws Exception {
    ParseContext ctx = new ParseContext();
    RecursiveParserWrapper parser = new RecursiveParserWrapper(AUTO_DETECT_PARSER);
    RecursiveParserWrapperHandler handler = new RecursiveParserWrapperHandler(new BasicContentHandlerFactory(BasicContentHandlerFactory.HANDLER_TYPE.IGNORE, -1), -1);
    Metadata rootMetadata = new Metadata();
    rootMetadata.add(TikaCoreProperties.RESOURCE_NAME_KEY, "testRTFRegularImages.rtf");
    try (TikaInputStream tis = TikaInputStream.get(getResourceAsStream("/test-documents/testRTFRegularImages.rtf"))) {
        parser.parse(tis, handler, rootMetadata, ctx);
    }
    List<Metadata> metadatas = handler.getMetadataList();
    //("testJPEG_EXIF_\u666E\u6797\u65AF\u987F.jpg");
    Metadata meta_jpg_exif = metadatas.get(1);
    //("testJPEG_\u666E\u6797\u65AF\u987F.jpg");
    Metadata meta_jpg = metadatas.get(3);
    assertTrue(meta_jpg_exif != null);
    assertTrue(meta_jpg != null);
    assertTrue(Arrays.asList(meta_jpg_exif.getValues(TikaCoreProperties.SUBJECT)).contains("serbor"));
    assertTrue(meta_jpg.get(TikaCoreProperties.COMMENTS).contains("Licensed to the Apache"));
    //make sure old metadata doesn't linger between objects
    assertFalse(Arrays.asList(meta_jpg.getValues(TikaCoreProperties.SUBJECT)).contains("serbor"));
    assertEquals("false", meta_jpg.get(RTFMetadata.THUMBNAIL));
    assertEquals("false", meta_jpg_exif.get(RTFMetadata.THUMBNAIL));
    //need flexibility for if tesseract is installed or not
    //TODO -- fix this test.  It is too fragile.
    assertTrue(meta_jpg.names().length >= 52 && meta_jpg.names().length <= 60);
    assertTrue(meta_jpg_exif.names().length >= 100 && meta_jpg_exif.names().length <= 130);
}

@Test
public void testBasicWord() throws Exception {
    List<Metadata> list = getRecursiveMetadata("testWORD2003.xml");
    assertEquals(8, list.size());
    //container doc
    Metadata m = list.get(0);
    String xml = m.get(TikaCoreProperties.TIKA_CONTENT);
    xml = xml.replaceAll("\\s+", " ");
    //make sure that metadata gets dumped to xml
    assertContains("<meta name=\"meta:character-count-with-spaces\" content=\"256\"", xml);
    //do not allow nested <p> elements
    assertContains("<img href=\"02000003.jpg\" />", xml);
    assertContains("<table><tbody>", xml);
    assertContains("</tbody></table>", xml);
    assertContains("<td><p>R1 c1</p> </td>", xml);
    assertContains("<a href=\"https://tika.apache.org/\">tika</a>", xml);
    assertContains("footnote", xml);
    assertContains("Mycomment", xml);
    assertContains("Figure 1: My Figure", xml);
    assertContains("myEndNote", xml);
    assertContains("We have always been at war with OceaniaEurasia", xml);
    assertContains("Text box", xml);
    assertNotContained("Text boxText box", xml);
    assertContains("MyHeader", xml);
    assertContains("MyFooter", xml);
    assertContains("<img href=\"02000003.jpg\" />", xml);
    assertEquals("219", m.get(Office.CHARACTER_COUNT));
    assertEquals("256", m.get(Office.CHARACTER_COUNT_WITH_SPACES));
    assertEquals("38", m.get(Office.WORD_COUNT));
    assertEquals("1", m.get(Office.PARAGRAPH_COUNT));
    assertEquals("Allison, Timothy B.", m.get(TikaCoreProperties.CREATOR));
    assertEquals("2016-04-27T17:49:00Z", m.get(TikaCoreProperties.CREATED));
    assertEquals("application/vnd.ms-wordml", m.get(Metadata.CONTENT_TYPE));
    //make sure embedded docs were properly processed
    assertContains("moscow-birds", Arrays.asList(list.get(7).getValues(TikaCoreProperties.SUBJECT)));
    assertEquals("testJPEG_EXIF.jpg", list.get(7).get(TikaCoreProperties.ORIGINAL_RESOURCE_NAME));
    //check that text is extracted with breaks between elements
    String txt = getText(getResourceAsStream("/test-documents/testWORD2003.xml"), AUTO_DETECT_PARSER);
    txt = txt.replaceAll("\\s+", " ");
    assertNotContained("beforeR1", txt);
    assertContains("R1 c1 R1 c2", txt);
    assertNotContained("footnoteFigure", txt);
    assertContains("footnote Figure", txt);
    assertContains("test space", txt);
}

@Test
public void testExample() throws Exception {
    Metadata m = new Metadata();
    PrintStream out = System.out;
    PrintStream err = System.err;
    ByteArrayOutputStream outBos = new ByteArrayOutputStream();
    ByteArrayOutputStream errBos = new ByteArrayOutputStream();
    PrintStream tmpOut = new PrintStream(outBos, true, UTF_8.toString());
    PrintStream tmpErr = new PrintStream(errBos, true, UTF_8.toString());
    System.setOut(tmpOut);
    System.setErr(tmpErr);
    try {
        assertThrowable("example.xml", m, IOException.class, "not another IOException");
        assertMockParser(m);
    } finally {
        System.setOut(out);
        System.setErr(err);
    }
    String outString = new String(outBos.toByteArray(), UTF_8);
    assertContains("writing to System.out", outString);
    String errString = new String(errBos.toByteArray(), UTF_8);
    assertContains("writing to System.err", errString);
}

