@Test
public void testInterruptibleSleep() {
    //Without static initialization of the parser, it can take ~1 second after t.start()
    //before the parser actually calls parse.  This is
    //just the time it takes to instantiate and call AutoDetectParser, do the detection, etc.
    //This is not thread creation overhead.
    ParserRunnable r = new ParserRunnable("sleep_interruptible.xml");
    Thread t = new Thread(r);
    t.start();
    long start = System.currentTimeMillis();
    try {
        Thread.sleep(1000);
    } catch (InterruptedException e) {
        //swallow
    }
    t.interrupt();
    try {
        t.join(10000);
    } catch (InterruptedException e) {
        //swallow
    }
    long elapsed = System.currentTimeMillis() - start;
    //the xml file specifies 3000
    boolean shortEnough = elapsed < 2000;
    assertTrue(shortEnough, "elapsed (" + elapsed + " millis) was not short enough");
}

@Test
public void testNonInterruptibleSleep() {
    ParserRunnable r = new ParserRunnable("sleep_not_interruptible.xml");
    Thread t = new Thread(r);
    t.start();
    long start = System.currentTimeMillis();
    try {
        //make sure that the thread has actually started
        Thread.sleep(1000);
    } catch (InterruptedException e) {
        //swallow
    }
    t.interrupt();
    try {
        t.join(20000);
    } catch (InterruptedException e) {
        //swallow
    }
    long elapsed = System.currentTimeMillis() - start;
    //the xml file specifies 3000, this sleeps 1000
    boolean longEnough = elapsed >= 3000;
    assertTrue(longEnough, "elapsed (" + elapsed + " millis) was not long enough");
}

/*
    Check that if Tesseract is told to skip OCR,
    the TesseractOCRParser claims to not support
    any file types. So, the standard image parser is called instead.
     */
@Test
public void offersNoTypesIfNotFound() throws Exception {
    TesseractOCRParser parser = new TesseractOCRParser();
    DefaultParser defaultParser = new DefaultParser();
    MediaType png = MediaType.image("png");
    // With an invalid path, will offer no types
    TesseractOCRConfig skipOcrConfig = new TesseractOCRConfig();
    skipOcrConfig.setSkipOcr(true);
    ParseContext parseContext = new ParseContext();
    parseContext.set(TesseractOCRConfig.class, skipOcrConfig);
    // No types offered
    assertEquals(0, parser.getSupportedTypes(parseContext).size());
    // And DefaultParser won't use us
    assertEquals(ImageParser.class, defaultParser.getParsers(parseContext).get(png).getClass());
}

