    response = WebClient.create(endPoint + TIKA_PATH).type("application/pdf").accept("text/plain").header(TesseractServerConfig.X_TIKA_OCR_HEADER_PREFIX.toLowerCase(Locale.ROOT) + "skipocr", "true").put(ClassLoader.getSystemResourceAsStream("test-documents/testOCR.pdf"));
