@Test
public void testPDFPerPageRenderColor() throws Exception {
    Response response = WebClient.create(CXFTestBase.endPoint + ALL_PATH).header(PDFServerConfig.X_TIKA_PDF_HEADER_PREFIX + "imageStrategy", "RenderPagesAtPageEnd").header(PDFServerConfig.X_TIKA_PDF_HEADER_PREFIX + "ocrImageType", "rgb").accept("application/zip").put(ClassLoader.getSystemResourceAsStream("test-documents/testColorRendering.pdf"));
    Map<String, byte[]> results = readZipArchiveBytes((InputStream) response.getEntity());
    byte[] renderedImage = null;
    for (Map.Entry<String, byte[]> e : results.entrySet()) {
        if (e.getKey().startsWith("tika-pdfbox-rendering")) {
            renderedImage = e.getValue();
            break;
        }
    }
    assertEquals("image/png", TikaConfig.getDefaultConfig().getDetector().detect(new ByteArrayInputStream(renderedImage), new Metadata()).toString());
    try (InputStream is = new ByteArrayInputStream(renderedImage)) {
        BufferedImage image = ImageIO.read(is);
        //top left
        AverageColor averageColor = getAverageColor(image, 0, image.getWidth() / 5, 0, image.getHeight() / 10);
        assertTrue(averageColor.getRed() > 250);
        assertTrue(averageColor.getGreen() < 1);
        assertTrue(averageColor.getBlue() < 1);
        //bottom left = green
        averageColor = getAverageColor(image, 0, image.getWidth() / 5, image.getHeight() / 2 + image.getHeight() / 10, image.getHeight() / 2 + 2 * image.getHeight() / 10);
        assertTrue(averageColor.getRed() < 1);
        assertTrue(averageColor.getGreen() > 250);
        assertTrue(averageColor.getBlue() < 1);
        //bottom right = blue
        averageColor = getAverageColor(image, image.getWidth() / 2 + image.getWidth() / 10, image.getWidth() / 2 + 2 * image.getWidth() / 10, image.getHeight() / 2 + image.getHeight() / 10, image.getHeight() / 2 + 2 * image.getHeight() / 10);
        assertTrue(averageColor.getRed() < 1);
        assertTrue(averageColor.getGreen() < 1);
        assertTrue(averageColor.getBlue() > 250);
    }
}

